import React, { useState } from "react";
// import { CiEdit } from "react-icons/ci";
import "../assets/css/update_popup.css";
import { RxCross2 } from "react-icons/rx";


const UpdatePage = ({setshowPopUp , updatedContent}) => {

  const [input, setInput] = useState(updatedContent?.text);
  const [content, setContent] = useState("");

  const [todos, setTodos] = useState([]);
//   const [content, setContent] = useState("");
  // const [editingTodo, setEditingTodo] = useState(null);
//   const [showPopUp, setshowPopUp] = useState(false);
//   const [updatedContent, setupdatedContent] = useState({});

  const editTodo = async (e, todoId) => {
    e.preventDefault();
    const res = await fetch(`/api/todos/${todoId}`, {
      method: "PUT",
      body: JSON.stringify({ todo: content }),
      headers: {
        "Content-Type": "application/json",
      },
    });
  
    const updatedTodo = await res.json();
  
    if (updatedTodo.acknowledged) {
      setTodos((currentTodos) => {
        return currentTodos.map((currentTodo) => {
          if (currentTodo._id === todoId) {
            return { ...currentTodo, todo: content };
          }
          return currentTodo;
        });
      });
      setContent("");
      setEditingTodo(null); // Clear editing state after editing
    }
  };
  


  return (
    <div className="backdrop">
      <div className="popup">
          <RxCross2 className='edit-btn' onClick={ () => setshowPopUp(false)} />
        <div className="update-title">Update Todo</div>
        <div className="edit_section">
          <div className="popup__input_holder">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Update your Todo..."
            />
          </div>
          <button className="update-btn" onClick={(e) => editTodo(e, todo._id)} >Update</button>
        </div>
      </div>
    </div>
  );
};


export default UpdatePage;
